//----------------importing necessary files
import React from "react";
import ReactDOM from "react-dom";

//CSS
import "./index.css";

//stateless functional component
//always return JSX

// same thing can be done as but gets messy if im return alot:

// const Greetings = () => {
//   return React.createElement(
//     "div",
//     {},
//     React.createElement("h1", {}, "Check me out, boy!")
//   );
// };

//-------------------Setting up vars

let title = "The Boy, The Mole, The Fox and the Horse";
const author = "Charlie Mackesy";
let Image = () => (
  <img
    src="https://images-eu.ssl-images-amazon.com/images/I/81fKY3dmCCL._AC_UL200_SR200,200_.jpg"
    alt=""
  />
);

//-------------------my first component
const Book = () => {
  return (
    <article className="book">
      <Image />
      <h3>{title}</h3>
      <h2 classname="author" style={{ fontSize: "1.2rem", color: "red" }}>
        {author.toUpperCase()}
      </h2>
    </article>
  );
};

function BookList() {
  return (
    <section className="bookList">
      <Book />
      <Book />
      <Book />
      <Book />
    </section>
  );
}

// const Image = () => <img src="https://picsum.photos/200" alt="" />;

ReactDOM.render(<BookList />, document.getElementById("root"));
