//my first component

import React from "react";
import ReactDOM from "react-dom";

//CSS
import "./index.css";

//stateless functional component
//always return JSX

// same thing can be done as but gets messy if im return alot:

// const Greetings = () => {
//   return React.createElement(
//     "div",
//     {},
//     React.createElement("h1", {}, "Check me out, boy!")
//   );
// };

function BookList() {
  return (
    <section className="bookList">
      <Book />
      <Book />
      <Book />
      <Book />
      <Book />
      <Book />
      <Book />
      <Book />
    </section>
  );
}

const Book = () => {
  return (
    <article className="book">
      <Image></Image>
      <Title />
      <Author />
    </article>
  );
};

const Image = () => (
  <img
    src="https://images-eu.ssl-images-amazon.com/images/I/81fKY3dmCCL._AC_UL200_SR200,200_.jpg"
    alt=""
  />
);

const Title = () => (
  <h3 style={{ color: "#1f5070", fontSize: ".9rem" }}>
    The Boy, The Mole, The Fox and The Horse{" "}
  </h3>
);
const Author = () => (
  <h4 style={{ color: "#617d14", fontSize: "0.95rem" }}>Charlie Mackesy</h4>
);

// const Image = () => <img src="https://picsum.photos/200" alt="" />;

ReactDOM.render(<BookList />, document.getElementById("root"));
